u32 VIRTUAL_FW_SIZE =0;

/******************************************************************************/
/******************************************************************************/
/***                       Functions declare                   		         **/
/******************************************************************************/
/******************************************************************************/
int _send_fw(void){

	return 0;
}

/******************************************************************************/
/******************************************************************************/
/***                            Functions callback                           **/
/******************************************************************************/
/******************************************************************************/

void TEST_virtual_fw(u32 _fwsize) {
	if (blt_soft_timer_find(_send_fw) == -1) {
		ERR(APP,"TEST FOTA (%d bytes)!!!\r\n",_fwsize);
		VIRTUAL_FW_SIZE = _fwsize;
		blt_soft_timer_add(&_send_fw,50 * 1003);
	}
}

